import { BrowserRouter, Routes, Route } from 'react-router-dom';
import NavBar from './components/NavBar';
import ItemListContainer from './components/ItemListContainer'; // Importamos el componente de la página de inicio
import './css/App.css';

// Importa los demás componentes de las páginas que usarás con React Router
import Menu from './pages/Menu';
import Combos from './pages/Combos';
import Promociones from './pages/Promociones';
import Contacto from './pages/Contacto';

function App() {
    const greeting = "¡Bienvenido a El Gordo Burguer!";

    return (
        <BrowserRouter>
            <NavBar />
            {/* La página de inicio (ItemListContainer) se renderiza aquí.
        Estará siempre visible, a menos que la URL cambie y se muestre un Route.
      */}
            <ItemListContainer greeting={greeting} />

            {/* Las rutas para las otras páginas se definen aquí.
        El contenido de estas páginas se mostrará debajo de ItemListContainer.
      */}
            <Routes>
                <Route path="/menu" element={<Menu />} />
                <Route path="/combos" element={<Combos />} />
                <Route path="/promociones" element={<Promociones />} />
                <Route path="/contacto" element={<Contacto />} />
            </Routes>
        </BrowserRouter>
    );
}

export default App;
